allow:
Bash(echo "

/* ============================================
   DATATABLE
   ============================================ */

.datatable-wrapper {
  background: white;
  border: 1px solid var\(--border\);
  border-radius: var\(--radius\);
}

.datatable-controls {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.875rem 1.125rem;
  border-bottom: 1px solid var\(--border\);
  background: #fafafa;
}

.datatable-search {
  position: relative;
  flex: 1;
  max-width: 300px;
}

.datatable-search i {
  position: absolute;
  left: 0.625rem;
  top: 50%;
  transform: translateY\(-50%\);
  color: var\(--gray\);
  font-size: 0.8125rem;
}

.datatable-search input {
  padding-left: 2.25rem;
  width: 100%;
}

.datatable-info {
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.datatable-content {
  overflow-x: auto;
}

.datatable-content .table {
  margin-bottom: 0;
}

.table th.sortable {
  cursor: pointer;
  user-select: none;
  transition: var\(--transition\);
}

.table th.sortable:hover {
  background: #f3f4f6;
  color: var\(--dark\);
}

.table th.sortable i {
  margin-left: 0.375rem;
  font-size: 0.75rem;
  color: var\(--light-gray\);
}

.table-actions {
  display: flex;
  gap: 0.375rem;
  flex-wrap: nowrap;
}

.datatable-pagination {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.75rem 1.125rem;
  border-top: 1px solid var\(--border\);
  background: #fafafa;
}

.pagination-info {
  font-size: 0.8125rem;
  color: var\(--gray\);
}

.pagination-buttons {
  display: flex;
  gap: 0.375rem;
  align-items: center;
}

.pagination-buttons .btn {
  min-width: 32px;
  padding: 0.375rem 0.5rem;
}
")

